const {zJuqnError} = require('../Util/zJuqnError')
/**
 * @param {String} avatar 
 * @returns {Promise<string>} 
 */
 module.exports = async function boostCard(avatar) {
    if (!avatar) throw new zJuqnError("[zJuqn.Error] Debes ingresar el link del avatar en la boostCard");
    if(!avatar.endsWith('.png' || '.jpg')) throw new zJuqnError("[zJuqn.Error] El avatar de la boostCard debe ser en formato png o jpg")

    try {
       return `https://frenchnoodles.xyz/api/endpoints/boostercard/?image=${avatar}`
    } catch(err) {
        throw new zJuqnError("[zJuqn.Error] Hubo un error a el enviar adjuntar el link de la boost card porfavor intentalo de nuevo mas tarde. Si el problema persiste contacta con mi creador")
    }


}