const { MessageAttachment } = require('discord.js')

/**
 * Funcion para simular un logro de minecraft
 * @param {String} logro
 */

async function minecraftLogro(logro) {
if(!logro) throw new SyntaxError("[zJuqn.Error] | En la funcion minecraftLogro debes ingresar el logro obtenido")
if(logro.length > 20) throw new SyntaxError("[zJuqn.Error] | En la funcion minecraftLogro el nombre del logro no debe contener mas de 16 caracteres")
const attachemnt = new MessageAttachment(`https://minecraftskinstealer.com/achievement/10/Logro%20Desbloqueado/${logro}`, "minecraftLogro.png")
return attachemnt
}

module.exports = minecraftLogro