/**
 * Generador de contraseñas
 * @param {Number} Length 
 * @returns {String}
 */
 module.exports = (Cantidad) => {


    if (!Cantidad) throw new TypeError("[API ERROR] | La cantidad no esta definido")
    var length = Cantidad,
        token = '123456789abcdefghijklmnñopqrstuvwxyzABCDFGHIJKLMNÑOPQRSTUVXYZ',
            value = "";
    for (var i = 0, n = token.length; i < length; ++i) {
        value += token.charAt(Math.floor(Math.random() * n));
    }
    return value;

 //   for (let i = 0; i < num; i++){
   // result1 += token.charAt(Math.floor(Math.random() * charactersLength));
//}

}